export const AddTask = ({tasklist, setTasklist, task, setTask}) => {

    const handleSubmit= (e)=>{
        e.preventDefault();
        if(task.id){
            const date = new Date();
            const updateTask = tasklist.map((todo)=>(
                todo.id==task.id
            ))
        }else{
            const date = new Date();
            const newTask = {
                id: date.getTime(),
                name: e.target.task.value,
                time: `${date.toLocaleTimeString()} ${date.toLocaleDateString()}`
            };

            setTasklist([...tasklist, newTask]);
            e.target.task.value = "";
        }
    }

  return (
   <section className="addTask">
        {/* After creating UI you just need to write a function handleSubmit to add new task */}
        <form onSubmit={handleSubmit}>
            <input type="text" value={task.name} name="task" autoComplete="off" placeholder="add task" maxLength={25}/>
            <button type="submit">Add</button>
        </form>
   </section>
  )
}
