import { useEffect, useState } from 'react';
import Logo from '../assets/logo.svg';

export const Header = () => {
    const [theme,setTheme] = useState("light");

    useEffect(()=>{
        document.documentElement.removeAttribute("class");
        document.documentElement.classList.add(theme);
    },[theme]);



  return (
    <header>
        <div className="logo">
            <img src={Logo} alt="TaskList Logo" />
            <span>TaskList</span>
        </div>
        <div className="themeSelector">
            < span onClick = {
                () => setTheme("light")
            }
            className = {
                    theme === "light" ? "light activeTheme" : "light"
                } > < /span> <
                span onClick = {
                    () => setTheme("medium")
                }
            className = {
                    theme === "light" ? "light activeTheme" : "light"
                } > < /span> <
                span onClick = {
                    () => setTheme("dark")
                }
            className = {
                    theme === "light" ? "light activeTheme" : "light"
                } > < /span> <
                span onClick = {
                    () => setTheme("gOne")
                }
            className = {
                    theme === "light" ? "light activeTheme" : "light"
                } > < /span> <
                span onClick = {
                    () => setTheme("gTwo")
                }
            className = {
                    theme === "light" ? "light activeTheme" : "light"
                } > < /span> <
                span onClick = {
                    () => setTheme("gThree")
                }
            className = {
                theme === "light" ? "light activeTheme" : "light"
            } > < /span>
        </div>
    </header>
  )
}
