import { useEffect, useState } from 'react';
import Logo from '../assets/logo.svg';

export const Header = () => {
    const [theme,setTheme] = useState("light");

    useEffect(()=>{
        document.documentElement.classList.add(theme);
    },[theme]);



  return (
    <header>
        <div className="logo">
            <img src={Logo} alt="TaskList Logo" />
            <span>TaskList</span>
        </div>
        <div className="themeSelector">
            <span onClick={()=>setTheme("light")} className="light"></span>
            <span onClick={()=>setTheme("medium")} className="medium"></span>
            <span onClick={()=>setTheme("dark")} className="dark"></span>
            <span onClick={()=>setTheme("gOne")} className="gOne"></span>
            <span onClick={()=>setTheme("light")} className="gTwo"></span>
            <span onClick={()=>setTheme("light")} className="gThree"></span>
        </div>
    </header>
  )
}
